/**create */

const program = require('commander')

const {
    createVueProjectAction,
    addComponentAction,
    addPageAndRouteAction,
    addStoreAction
} = require('./actions')

/**
 * 主要的指令创建
 */
const createCommamd = () => {
    program.command('create <project> [others...]')
        .description('clone repo into a folder')
        .action(createVueProjectAction)

    program.command('addcpn <name>')
        .description('add vue component, eg: addcpn NavBar [-d src/components]')
        .action((name) => {
            addComponentAction(name, program.dest || 'src/components')
        })

    program.command('addpage <page>')
        .description('add vue page and router config')
        .action((page) => {
            addPageAndRouteAction(page, program.dest || 'src/pages')
        })

    program.command('addstore <store>')
        .description('add a store and type file')
        .action((store) => {
            addStoreAction(store, program.dest || 'src/store/modules')
        })
}

module.exports = createCommamd